 isLoading = false
 navController.navigate(Screens.CompleteProfile.route) {
 popUpTo(Screens.SignIn.route) { inclusive = true }
 }
 }
 is AuthState.Error -> {
 isLoading = false
 val error = (authState as AuthState.Error).message
 Log.e("SignInScreen", "Auth error: $error")
 errorMessage = error
 }
 else -> {
