 Log.d("SignInScreen", "Got Google account: ${account?.email}")

 account?.idToken?.let { token ->
 Log.d("SignInScreen", "Got Google Sign In token, starting authentication...")
 authViewModel.handleGoogleSignInResult(token) { success ->
 isLoading = false
 if (!success) {
