// Initialize phoneNumberUtil lazily
private var phoneNumberUtil: PhoneNumberUtil? = null

// Add a method to validate and format phone number
fun validateAndFormatPhoneNumber(context: Context, phoneNumber: String): Triple<Boolean, String, String> {
 // Initialize phoneNumberUtil if needed
 if (phoneNumberUtil == null) {
 phoneNumberUtil = PhoneNumberUtil.createInstance(context)
 }

 try {
 val parsedNumber = phoneNumberUtil!!.parse(phoneNumber, null)

 if (!phoneNumberUtil!!.isValidNumber(parsedNumber)) {
 return Triple(false, "Please enter a valid phone number", "")
 }

 // Format the phone number to E164 format for Firebase
 val formattedNumber = phoneNumberUtil!!.format(parsedNumber, PhoneNumberUtil.PhoneNumberFormat.E164)
 return Triple(true, "", formattedNumber)

 } catch (e: NumberParseException) {
 return Triple(false, "Invalid phone number format. Please include country code (+XX)", "")
 } catch (e: Exception) {
 return Triple(false, "Error processing phone number: ${e.message}", "")
 }
}

// Update startPhoneNumberVerification to use the validation method
fun startPhoneNumberVerification(context: Context, phoneNumber: String, activity: Activity) {
 val (isValid, errorMessage, formattedNumber) = validateAndFormatPhoneNumber(context, phoneNumber)

 if (!isValid) {
 _phoneAuthState.value = PhoneAuthState.Error(errorMessage)
 return
 }

 _phoneAuthState.value = PhoneAuthState.Loading

 val options = PhoneAuthOptions.newBuilder(auth)
 .setPhoneNumber(formattedNumber)
 .setTimeout(60L, TimeUnit.SECONDS)
 .setActivity(activity)
 .setCallbacks(callbacks)
 .build()

 PhoneAuthProvider.verifyPhoneNumber(options)
}
```

