## 7. Error Handling in Auth Flows

To prevent crashes, make sure error handling is robust in all authentication methods:

```kotlin:app/src/main/java/android/saswat/viewModel/Auth View Model.kt
// Inside AuthViewModel class

fun signInWithEmailPassword(email: String, password: String, onComplete: (Boolean) -> Unit) {
 if (email.isBlank() || password.isBlank()) {
 _authState.value = AuthState.Error("Email and password cannot be empty")
 onComplete(false)
 return
 }

 viewModelScope.launch {
 try {
 _authState.value = AuthState.Loading
 val result = auth.signInWithEmailAndPassword(email, password).await()
 result.user?.let {
 fetchUserData()
 _authState.value = AuthState.Success
 onComplete(true)
 } ?: run {
 // Handle the edge case where user is null but no exception
 _authState.value = AuthState.Error("Authentication failed. Please try again.")
 onComplete(false)
 }
 } catch (e: Exception) {
 _authState.value = AuthState.Error(e.message ?: "Authentication failed")
 onComplete(false)
 }
 }
}

fun handleGoogleSignInResult(idToken: String, onComplete: (Boolean) -> Unit) {
 if (idToken.isBlank()) {
 _authState.value = AuthState.Error("Google authentication failed. Invalid token.")
 onComplete(false)
 return
 }

 viewModelScope.launch {
 try {
 _authState.value = AuthState.Loading
 val credential = GoogleAuthProvider.getCredential(idToken, null)
 val authResult = auth.signInWithCredential(credential).await()
 val user = authResult.user ?: throw Exception("Failed to sign in: No user returned")
 val isNewUser = authResult.additionalUserInfo?.isNewUser == true

 if (isNewUser) {
 // Create a new user record in Firestore
 val userData = UserData(
 username = user.displayName ?: "",
 email = user.email ?: "",
 userId = user.uid,
 profileImageUrl = user.photoUrl?.toString() ?: "",
 authProvider = "google"
 )

 try {
 firestore.collection("users").document(user.uid).set(userData).await()
 _userData.value = userData
 _authState.value = AuthState.NeedsProfileCompletion
 onComplete(true)
 } catch (e: Exception) {
 _authState.value = AuthState.Error("Failed to create user profile: ${e.message}")
 onComplete(false)
 }
 } else {
 // Fetch existing user data
 fetchUserData()
 _authState.value = AuthState.Success
 onComplete(true)
 }
 } catch (e: Exception) {
 Log.e("AuthViewModel", "Google sign in failed: ${e.localizedMessage}", e)
 _authState.value = AuthState.Error(e.message ?: "Google sign in failed")
 onComplete(false)
 }
 }
}
