@OptIn(ExperimentalMaterial3Api::class)
@Composable
fun VerifyPhoneScreen(
 navController: NavController,
 phoneNumber: String
) {
 val context = LocalContext.current

 val phoneAuthViewModel: PhoneAuthViewModel = viewModel(
 factory = PhoneAuthViewModelFactory(context.applicationContext)
 )

 var verificationCode by remember { mutableStateOf("") }
 var errorMessage by remember { mutableStateOf<String?>(null) }
 var timeRemaining by remember { mutableStateOf(60) }
 var canResend by remember { mutableStateOf(false) }

 val phoneAuthState by phoneAuthViewModel.phoneAuthState.collectAsState()

 // Countdown timer for resend button
 LaunchedEffect(Unit) {
 while (timeRemaining > 0) {
 delay(1000)
 timeRemaining--
 }
 canResend = true
 }

 // Monitor phone auth states for navigation
 LaunchedEffect(phoneAuthState) {
 when (phoneAuthState) {
 is PhoneAuthState.Authenticated -> {
 val isNewUser = (phoneAuthState as PhoneAuthState.Authenticated).isNewUser
 if (isNewUser) {
 navController.navigate("completeProfile") {
 popUpTo("signIn") { inclusive = true }
 }
 } else {
 navController.navigate("main") {
 popUpTo("signIn") { inclusive = true }
 }
 }
 }
 is PhoneAuthState.Error -> {
 errorMessage = (phoneAuthState as PhoneAuthState.Error).message
 }
 else -> {}
 }
 }

 Scaffold(
 topBar = {
 TopAppBar(
 title = { Text("Verify Phone") },
 navigationIcon = {
 IconButton(onClick = { navController.popBackStack() }) {
 Icon(Icons.Default.ArrowBack, contentDescription = "Back")
 }
 }
 )
 }
 ) { paddingValues ->
 Column(
 modifier = Modifier
 .fillMaxSize()
 .padding(paddingValues)
 .padding(horizontal = 24.dp),
 horizontalAlignment = Alignment.CenterHorizontally
 ) {
 Spacer(modifier = Modifier.height(40.dp))

 // BrewNet Logo
 Image(
 painter = painterResource(id = R.drawable.logo),
 contentDescription = "BrewNet Logo",
 modifier = Modifier.size(64.dp)
 )

 Spacer(modifier = Modifier.height(40.dp))

 // Verification heading
 Text(
 text = "Verification Code",
 fontSize = 24.sp,
 fontWeight = FontWeight.Bold,
 textAlign = TextAlign.Center
 )

 Spacer(modifier = Modifier.height(16.dp))

 // Instruction text
 Text(
 text = "We have sent a verification code to\n$phoneNumber",
 fontSize = 16.sp,
 color = Color.Gray,
 textAlign = TextAlign.Center
 )

 Spacer(modifier = Modifier.height(40.dp))

 // Verification code input
 OutlinedTextField(
 value = verificationCode,
 onValueChange = { 
 // Only allow 6 digits
 if (it.length <= 6 && it.all { char -> char.isDigit() }) {
 verificationCode = it 
 }
 },
 modifier = Modifier.fillMaxWidth(),
 label = { Text("6-digit code") },
 shape = RoundedCornerShape(16.dp),
 colors = OutlinedTextFieldDefaults.colors(
 unfocusedBorderColor = Color.LightGray,
 focusedBorderColor = Color(0xFF2196F3)
 ),
 singleLine = true,
 keyboardOptions = KeyboardOptions(
 keyboardType = KeyboardType.Number
 )
 )

 // Error message
 errorMessage?.let {
 Text(
 text = it,
 color = Color.Red,
 fontSize = 14.sp,
 modifier = Modifier.padding(top = 8.dp)
 )
 }

 Spacer(modifier = Modifier.height(24.dp))

 // Verify button
 Button(
 onClick = {
 phoneAuthViewModel.verifyPhoneNumberWithCode(
 code = verificationCode
 ) { success ->
 if (!success) {
 errorMessage = "Failed to verify code. Please try again."
 }
 }
 },
 modifier = Modifier
 .fillMaxWidth()
 .height(56.dp),
 shape = RoundedCornerShape(28.dp),
 colors = ButtonDefaults.buttonColors(
 containerColor = Color(0xFF2196F3)
 ),
 enabled = verificationCode.length == 6 && phoneAuthState !is PhoneAuthState.Loading
 ) {
 if (phoneAuthState is PhoneAuthState.Loading) {
 CircularProgressIndicator(
 color = Color.White,
 modifier = Modifier.size(24.dp)
 )
 } else {
 Text(
 "Verify Code",
 fontSize = 18.sp,
 fontWeight = FontWeight.Medium
 )
 }
 }

 Spacer(modifier = Modifier.height(24.dp))

 // Resend code option
 if (canResend) {
 TextButton(
 onClick = {
 phoneAuthViewModel.resendVerificationCode(
 phoneNumber = phoneNumber,
 activity = context as android.app.Activity
 )
 // Reset timer
 timeRemaining = 60
 canResend = false
 }
 ) {
 Text(
 text = "Resend Code",
 color = Color(0xFF2196F3),
 fontWeight = FontWeight.Medium
 )
 }
 } else {
 Text(
 text = "Resend code in ${timeRemaining}s",
 color = Color.Gray,
 fontSize = 14.sp
 )
 }
 }
 }
}

