import androidx.compose.material.icons.filled.Close
import androidx.compose.material.icons.filled.Search
import androidx.compose.material3.*
import androidx.compose.runtime.*
import androidx.compose.ui.Alignment
import androidx.compose.ui.Modifier
import androidx.compose.ui.draw.clip
import androidx.compose.ui.graphics.Color
import androidx.compose.ui.platform.LocalContext
import androidx.compose.ui.res.painterResource
import androidx.compose.ui.text.TextStyle
import androidx.compose.ui.text.font.FontWeight
import androidx.compose.ui.text.input.KeyboardType
import androidx.compose.ui.text.style.TextAlign
import androidx.compose.ui.unit.dp
import androidx.compose.ui.unit.sp
import androidx.compose.ui.window.Dialog
import androidx.core.text.isDigitsOnly
import androidx.lifecycle.viewmodel.compose.viewModel
import androidx.navigation.NavController
import androidx.navigation.compose.rememberNavController
import com.google.android.gms.auth.api.signin.GoogleSignIn
import com.google.android.gms.auth.api.signin.GoogleSignInOptions
import com.google.android.gms.common.api.ApiException
import io.michaelrocks.libphonenumber.android.PhoneNumberUtil
import java.util.*

// Data class for country code info
data class CountryCode(
 val name: String,
 val code: String,
 val flagResId: Int,
 val phoneCode: String
)

@Composable
fun SignInScreen(
 navController: NavController,
 authViewModel: AuthViewModel = viewModel(),
 onSignUpClick: () -> Unit = {},
 onEmailSignInClick: () -> Unit = {}
) {
 val context = LocalContext.current

 // Set up phone auth view model with context
 val phoneAuthViewModel: PhoneAuthViewModel = viewModel(
 factory = PhoneAuthViewModelFactory(context.applicationContext)
 )

 // Initialize PhoneNumberUtil
 val phoneNumberUtil = remember { PhoneNumberUtil.createInstance(context) }

 // States for the UI
 var phoneNumber by remember { mutableStateOf("") }
 var selectedCountryCode by remember { mutableStateOf("+91") }
 var selectedCountryFlag by remember { mutableStateOf(R.drawable.india_flag) }
 var showCountryPicker by remember { mutableStateOf(false) }
 var errorMessage by remember { mutableStateOf<String?>(null) }
 var searchQuery by remember { mutableStateOf("") }

 // Collect states from view models
 val authState by authViewModel.authState.collectAsState()
 val phoneAuthState by phoneAuthViewModel.phoneAuthState.collectAsState()

 // Define list of country codes
 val countryCodes = remember {
 listOf(
 CountryCode("India", "IN", R.drawable.india_flag, "+91"),
 CountryCode("United States", "US", R.drawable.us_flag, "+1"),
 CountryCode("United Kingdom", "GB", R.drawable.uk_flag, "+44"),
 CountryCode("Canada", "CA", R.drawable.canada_flag, "+1"),
 CountryCode("Australia", "AU", R.drawable.australia_flag, "+61"),
 CountryCode("Germany", "DE", R.drawable.germany_flag, "+49"),
 CountryCode("France", "FR", R.drawable.france_flag, "+33"),
 CountryCode("Italy", "IT", R.drawable.italy_flag, "+39"),
 CountryCode("Japan", "JP", R.drawable.japan_flag, "+81"),
 CountryCode("Brazil", "BR", R.drawable.brazil_flag, "+55"),
 CountryCode("China", "CN", R.drawable.china_flag, "+86"),
 CountryCode("Russia", "RU", R.drawable.russia_flag, "+7"),
 CountryCode("Mexico", "MX", R.drawable.mexico_flag, "+52"),
 CountryCode("South Korea", "KR", R.drawable.south_korea_flag, "+82"),
 CountryCode("Singapore", "SG", R.drawable.singapore_flag, "+65"),
 CountryCode("Spain", "ES", R.drawable.spain_flag, "+34"),
 CountryCode("Netherlands", "NL", R.drawable.netherlands_flag, "+31"),
 )
 }

 // Filtered country codes based on search
 val filteredCountryCodes = remember(searchQuery) {
 if (searchQuery.isEmpty()) {
 countryCodes
 } else {
 countryCodes.filter { 
 it.name.contains(searchQuery, ignoreCase = true) || 
 it.code.contains(searchQuery, ignoreCase = true) ||
 it.phoneCode.contains(searchQuery, ignoreCase = true)
 }
 }
 }

 // Google Sign In setup
 val googleSignInClient = remember {
 val gso = GoogleSignInOptions.Builder(GoogleSignInOptions.DEFAULT_SIGN_IN)
 .requestIdToken(context.getString(R.string.default_web_client_id))
 .requestEmail()
 .build()
 GoogleSignIn.getClient(context, gso)
 }

 val googleSignInLauncher = rememberLauncherForActivityResult(
 contract = ActivityResultContracts.StartActivityForResult()
 ) { result ->
 if (result.resultCode == Activity.RESULT_OK) {
 try {
 val task = GoogleSignIn.getSignedInAccountFromIntent(result.data)
 val account = task.getResult(ApiException::class.java)
 account?.idToken?.let { token ->
 authViewModel.handleGoogleSignInResult(token) { success ->
 // Navigation handled by LaunchedEffect
 }
 }
 } catch (e: Exception) {
 errorMessage = "Google sign-in failed: ${e.message}"
 }
 }
 }

 // Monitor auth states for navigation
 LaunchedEffect(authState) {
 when (authState) {
 is AuthState.Success -> {
 navController.navigate("main") {
 popUpTo(navController.graph.startDestinationId) { inclusive = true }
 }
 }
 is AuthState.NeedsProfileCompletion -> {
 navController.navigate("completeProfile")
 }
 is AuthState.Error -> {
 errorMessage = (authState as AuthState.Error).message
 }
 else -> {}
 }
 }

 // Monitor phone auth states for navigation
 LaunchedEffect(phoneAuthState) {
 when (phoneAuthState) {
 is PhoneAuthState.CodeSent -> {
 navController.navigate("verifyPhone/$selectedCountryCode$phoneNumber")
 }
 is PhoneAuthState.Authenticated -> {
 val isNewUser = (phoneAuthState as PhoneAuthState.Authenticated).isNewUser
 if (isNewUser) {
 navController.navigate("completeProfile")
 } else {
 navController.navigate("main") {
 popUpTo(navController.graph.startDestinationId) { inclusive = true }
 }
 }
 }
 is PhoneAuthState.Error -> {
 errorMessage = (phoneAuthState as PhoneAuthState.Error).message
 }
 else -> {}
 }
 }

