+private fun calculateDistance(
+ userLat1: Double?,
+ userLon1: Double?,
+ cardLat2: Double?,
+ cardLon2: Double?
+): String {
+ if (userLat1 == null || userLon1 == null || cardLat2 == null || cardLon2 == null) {
+ return "?"
+ }
+
+ val r = 6371 // Earth's radius in kilometers
+
+ val lat1 = Math.toRadians(userLat1)
+ val lon1 = Math.toRadians(userLon1)
+ val lat2 = Math.toRadians(cardLat2)
+ val lon2 = Math.toRadians(cardLon2)
+
+ val dlon = lon2 - lon1
+ val dlat = lat2 - lat1
+
