 _phoneAuthState.value = PhoneAuthState.ProfileCompleted
 onComplete(true)
 } catch (e: Exception) {
 Log.e("PhoneAuthViewModel", "Profile completion failed", e)
 _phoneAuthState.value = PhoneAuthState.Error(e.message ?: "Profile completion failed")
 onComplete(false)
 }
 }
 }

